mapper.select:
select queboard.questionNo, queboard.memId, queboard.title, queboard.regDate, queboard.view, tags, tagNos, queboard.vote, (select count(*) from replyboard where queboard.questionNo = replyboard.questionNo ) as replyCount from (select distinct qboard.questionNo, qboard.memId, qboard.title, qboard.regDate, qboard.view, qboard.vote from questionboard as qboard, questiontag as qtag
where qboard.questionNo = qtag.questionNo and qtag.tagNo IN
#{tag.tagNo}
) as queboard left join ( select questiontag.questionNo, group_concat( tag.tagName separator ' ') as tags , group_concat(questiontag.tagNo separator ' ') as tagNos from questiontag left join tag on questiontag.tagNo = tag.tagNo group by questiontag.questionNo) as b on queboard.questionNo = b.questionNo
join (select questionNo, tagNo from questiontag where tagNo=#{tagNo}) as c on queboard.questionNo = c.questionNo
order by view desc limit 0,99;

select queboard.questionNo, memId, title, regDate, view, tags, tagNos, vote, (select count(*) from replyboard where queboard.questionNo = replyboard.questionNo) as replyCount from ( select distinct qboard.questionNo, qboard.memId, qboard.title, qboard.regDate, qboard.view, qboard.vote from questionboard as qboard, questiontag as qtag
where qboard.questionNo = qtag.questionNo and qtag.tagNo IN
#{tag.tagNo}
) as queboard left join (select questionNo, group_concat(tagName separator ' ') as tags , group_concat(questiontag.tagNo separator ' ') as tagNos from questiontag left join tag on questiontag.tagNo = tag.tagNo group by questionNo) as b on queboard.questionNo = b.questionNo
title like CONCAT('%',#{keyword},'%')
order by ${order} desc limit #{firstRow}, #{endRow};

    select count(*) from (
			select distinct qboard.questionNo, qboard.memId, qboard.title, qboard.regDate, qboard.view, qboard.vote from questionboard as qboard, questiontag as qtag
			
			where qboard.questionNo = qtag.questionNo and 
			qtag.tagNo IN  
			
	         #{tag.tagNo}
	        
			
			) as queboard
    
		join (select questionNo, tagNo from questiontag where tagNo=#{tagNo}) as c
		on queboard.questionNo = c.questionNo
	
   
	   
			title like CONCAT('%',#{keyword},'%')
		
	;

select questionboard.questionNo, title from questionboard 
	join favquestion on questionboard.questionNo = favquestion.questionNo 
		where favquestion.memId=#{memberId}  
	order by favDate desc
limit 0,5;

mapper.insert:
INSERT INTO memquestionvote (questionNo, memberId, voteDate, voteUpDown)
VALUE (#{questionNo}, #{memId}, now(), 'up') on DUPLICATE KEY UPDATE voteDate = now(), voteUpDown='up'
VALUE (#{questionNo}, #{memId}, now(), 'down') on DUPLICATE KEY UPDATE voteDate = now(), voteUpDown='down' ;

mapper.select:
select  
COALESCE((select COALESCE(count(*),0) from memquestionvote where questionNo=#{questionNo} group by voteUpDown having voteUpDown='up'),0) -
COALESCE((select count(*) from memquestionvote where questionNo=#{questionNo} group by voteUpDown having voteUpDown='down'),0) as count;
